var app = angular.module('weatherApp', []);


app.controller('WeatherController', function($scope, $http) {
    $scope.weather = {};
  
    $http.get('https://api.open-meteo.com/v1/forecast?latitude=40.23&longitude=-111.66&daily=temperature_2m_max,temperature_2m_min,sunrise,sunset,uv_index_max,precipitation_sum,windspeed_10m_max&current_weather=true&temperature_unit=fahrenheit&windspeed_unit=mph&precipitation_unit=inch&timezone=GMT')
      .then(function(response) {
        $scope.weather = response.data.current;
        //console.log($scope.weather)
      });
  });